using System.Collections; using System.Collections.Generic; using UnityEngine;  public class TumbuhanUIManager : MonoBehaviour {  	public Light Matahari; 	public Animator Grow; 	public ParticleSystem Rain; 	public float fadeSpeed = 2f; 	public float highIntensity = 6f; 	public float lowIntensity = 0.0f; 	public float changeMargin = 0.2f; 	private bool lightison = false; 	private bool Rainison; 	private float targetIntensity;  	void Start() 	{ 		Matahari.intensity = 0f; 		targetIntensity = highIntensity; 		Rain.Stop (); 		Grow.SetBool ("isGrowing", false);  	}  	public void ButtonClicked() { 		lightison = true; 	}  	public void ButtonRain() 	{ 		Rainison = true; 		Rain.Play ();         GameObject.Find("DragDropUtility").SendMessage("PlantGrow"); 	}   	void Update() 	{ 		if (lightison) { 			Matahari.intensity = Mathf.Lerp (Matahari.intensity, targetIntensity, fadeSpeed * Time.deltaTime);  		}  else { 			Matahari.intensity = Mathf.Lerp (Matahari.intensity, 0f, fadeSpeed * Time.deltaTime); 		}  		if (lightison && Rainison) 			StartCoroutine (treeGrowing());  	}  	IEnumerator treeGrowing() 	{ 		yield return new WaitForSeconds (5); 		Grow.SetBool ("isGrowing", true); 	}  } 